import React, { useState } from 'react';
import {Paper, Avatar } from '@mui/material';
import { Card, CardActions, CardContent, Button, Typography } from '@mui/material';
import { styled } from '@mui/material/styles';
import { Container, Nav, Navbar, NavDropdown } from 'react-bootstrap'; 
import 'bootstrap/dist/css/bootstrap.css';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import styled1 from 'styled-components';
import AddIcon from '@mui/icons-material/Add'; // Import the AddIcon component
import MoreVertIcon from '@mui/icons-material/MoreVert';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { red } from '@mui/material/colors';
import {

    CardHeader,
    CardMedia,
    IconButton,
    TextField,
  } from '@mui/material';


const BigDiver = styled1.div`
  background-color: black;
  padding-bottom: 40%;
  padding-top: 5%;
  color: white;
`;

const Spacer = styled1.div`
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
`;

const Spacer1 = styled1.div`
display: flex;
text-align: center;
flex-direction: row;
justify-content: space-evenly;
padding-top: 10px;
flex-wrap: wrap;
overflow-y: scroll;
border: 1px solid black;
height: 400px;
`;


const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
}));

const ProfileAvatar = styled(Avatar)(({ theme }) => ({
  width: theme.spacing(12),
  height: theme.spacing(12),
  marginBottom: theme.spacing(2),
}));

function Profile() {

    const [cards, setCards] = useState([]);

    const handleAddCard = () => {
        const newCard = {
          title: "Sanji's Paella",
          subheader: 'September 14, 2016',
          image: 'image1.jpg', // Replace with the actual image source
          content: 'This impressive paella is a perfect party dish and a fun meal to cook together with your guests...',
        };

        setCards([...cards, newCard]);
      };
      const [editingIndex, setEditingIndex] = useState(null);

  const handleEditCard = (index) => {
    setEditingIndex(index);
  };

  const handleSaveCard = (index) => {
    setEditingIndex(null);
  };
  return (
    <>    <Navbar expand="lg" className="bg-body-tertiary">
    <Container>
      <Navbar.Brand href="/">Chibi Canvas</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto">
          <Nav.Link href="/">Home</Nav.Link>
          <Nav.Link href="/Contact">Contact</Nav.Link>
          <Nav.Link href="/Profile">Profile</Nav.Link>
          <Nav.Link href="/Contact">Contact</Nav.Link>

          <Nav.Link href="/ShoppingCart">          <ShoppingCartIcon /> </Nav.Link>


        </Nav>
      </Navbar.Collapse>
    </Container>
  </Navbar>
  <BigDiver>
    <Container maxWidth="sm">
      <StyledPaper elevation={3}>
        <ProfileAvatar src="/path/to/avatar.png" alt="User Avatar" />
        <Typography variant="h4" component="h1" gutterBottom>
          Welcome to Your Profile
        </Typography>
        <Typography variant="body1">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam vel finibus justo.
        </Typography>
        {/* Display more user information or relevant content */}
      </StyledPaper>
      <Spacer>
<Card>
        <CardContent>
          <Button
            variant="outlined"
            color="primary"
            startIcon={<AddIcon />}
            onClick={handleAddCard}
          >
            Add Art
          </Button>
        </CardContent>
      </Card>
      </Spacer>
    </Container>

    <Spacer1>
    {cards.map((card, index) => (
  <Card key={index} sx={{ maxWidth: 345 }}>
    <CardHeader
      avatar={
        <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
          R
        </Avatar>
      }
      action={
        <IconButton aria-label="settings">
          <MoreVertIcon />
        </IconButton>
      }
      title={
        editingIndex === index ? (
          <TextField
            value={editedTitle}
            onChange={(e) => setEditedTitle(e.target.value)}
          />
        ) : (
          card.title
        )
      }
      subheader={
        editingIndex === index ? (
          <TextField
            value={editedSubheader}
            onChange={(e) => setEditedSubheader(e.target.value)}
          />
        ) : (
          card.subheader
        )
      }
    />
    <CardMedia component="img" height="194" image={card.image} alt={card.title} />
    <CardContent>
      <Typography variant="body2" color="text.secondary">
        {editingIndex === index ? (
          <TextField
            multiline
            value={editedContent}
            onChange={(e) => setEditedContent(e.target.value)}
          />
        ) : (
          card.content
        )}
      </Typography>
    </CardContent>
    <CardActions disableSpacing>
      <IconButton aria-label="add to favorites">
        <FavoriteIcon />
      </IconButton>
      <IconButton aria-label="share">
        <ShareIcon />
      </IconButton>
      <IconButton aria-label="Add to cart" href="/ShoppingCart">
        {/* Add your cart icon */}
      </IconButton>
      <IconButton
        aria-label="show more"
        onClick={() => handleToggleEdit(index)}
      >
        {editingIndex === index ? 'Save' : <ExpandMoreIcon />}
      </IconButton>
    </CardActions>
  </Card>
))}


</Spacer1>



    </BigDiver>

    </>
  );
}

export default Profile;
