import React, { useState } from 'react';
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import styled1 from 'styled-components';
import image1 from './images/Sanji_and_Zeff_Cooking.webp'
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { Link } from 'react-router-dom';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import TextField from '@mui/material/TextField';
import DialogActions from '@mui/material/DialogActions';
import {useNavigate } from 'react-router-dom';
import { Container, Nav, Navbar, NavDropdown } from 'react-bootstrap'; 
import axios from 'axios';


import 'bootstrap/dist/css/bootstrap.css';




const CheckoutButton = styled1.button`
  background-color: #f0c14b;
  border: 1px solid #a88734;
  border-radius: 3px;
  color: #111;
  padding: 10px 20px;
  cursor: pointer;
  font-weight: bold;
  transition: background-color 0.3s, border-color 0.3s;

  &:hover {
    background-color: #ff9900;
    border-color: #9c7e31;
  }
`;






const Centerpage = styled1.div`
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BigDiver = styled1.div`
  background-color: black;
  padding-bottom: 30%;
  color: white;
`;


const Titler = styled1.div`
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 1%;  
  padding-bottom: 1%;  

`;

const LoginSpace = styled1.div`
text-align: center;
display: flex;
flex-direction: row;
align-items: center;
justify-content: center;

`;


const ExpandMore = styled((props) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));

function Home() {

  const navigate = useNavigate();

  const [expanded, setExpanded] = React.useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [email, setEmail] = useState('');
  const [isLoginDialogOpen, setIsLoginDialogOpen] = useState(false);
  const [isAccountDialogOpen, setIsAccountDialogOpen] = useState(false);
  const [password, setPassword] = useState('');
  const [isValidationError, setIsValidationError] = useState(false);


  const handleExpandClick = () => {
    setExpanded(!expanded);
  };



  const handleCreateAccountClick = () => {
    setIsDialogOpen(true);
  };

  const handleDialogClose = () => {
    setIsDialogOpen(false);
  };






  const handleLoginClick = () => {
    setIsLoginDialogOpen(true);
  };


  const handleProfileClick = () => {
if(setIsValidationError(false) && setIsLoginDialogOpen(false)){
  navigate('/Profile');

}

